    <title>Bus Trip Booking</title>
	<style>
	/* General Reset */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

/* Body */
body {
    font-family: 'Arial', sans-serif;
    background-color: #f0f4f8; /* Soft background */
    color: #333;
    background-image: url("bgbus.jpg"); /* Change this to a bus-themed background */
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
    font-size: 16px;
    line-height: 1.6;
}

/* Header */
.header {
    background-color: rgba(0, 0, 0, 0.6);
    color: #fff;
    padding: 80px 20px;
    text-align: center;
    box-shadow: 0 20px 50px rgba(0, 0, 0, 0.3);
    backdrop-filter: blur(15px);
}

.header .container {
    max-width: 1200px;
    margin: 0 auto;
}

h1 {
    font-size: 4rem;
    letter-spacing: 5px;
    text-transform: uppercase;
    text-shadow: 5px 5px 10px rgba(0, 0, 0, 0.5);
    color: #f39c12; /* Warm yellow */
    transition: transform 0.4s ease, color 0.4s ease;
}

h1:hover {
    transform: scale(1.1);
    color: #e74c3c; /* Red accent on hover */
}

/* Main Content */
.main-content {
    padding: 80px 20px;
    text-align: center;
    background-color: #fff;
    box-shadow: 0 -10px 40px rgba(0, 0, 0, 0.1);
}

.bus-list {
    margin-top: 30px;
}

.bus-container {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
    gap: 30px;
    justify-items: center;
}

.bus-card {
    background: linear-gradient(135deg, #ffffff, #f0f2f5);
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
    transition: transform 0.4s ease, box-shadow 0.4s ease;
    max-width: 350px;
    position: relative;
    cursor: pointer;
    background-color: #fff;
}

.bus-card:hover {
    transform: translateY(-20px);
    box-shadow: 0 30px 60px rgba(0, 0, 0, 0.2);
}

.bus-image {
    width: 100%;
    height: 250px;
    object-fit: cover;
    border-radius: 20px 20px 0 0;
    transition: opacity 0.4s ease;
}

.bus-card:hover .bus-image {
    opacity: 0.85;
}

.bus-details {
    padding: 25px;
    background-color: #fff;
    border-radius: 0 0 20px 20px;
}

.bus-title {
    font-size: 30px;
    color: #333;
    margin-bottom: 12px;
    font-weight: 700;
    text-transform: capitalize;
    transition: color 0.4s ease;
}

.bus-title:hover {
    color: #e67e22; /* Orange color */
}

.bus-route,
.bus-duration,
.bus-price {
    font-size: 18px;
    color: #7f8c8d;
    margin-bottom: 10px;
}

.book-button {
    background-color: #3498db; /* Vibrant blue */
    color: white;
    padding: 16px 32px;
    border-radius: 30px;
    text-decoration: none;
    display: inline-block;
    font-size: 20px;
    font-weight: 600;
    text-transform: uppercase;
    transition: background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease;
    margin-top: 20px;
}

.book-button:hover {
    background-color: #2980b9; /* Darker blue for hover */
    transform: scale(1.05);
    box-shadow: 0 10px 30px rgba(52, 152, 219, 0.4);
}

.book-button:active {
    transform: scale(1.02);
}

/* Footer */
.footer {
    background-color: rgba(0, 0, 0, 0.8);
    color: white;
    padding: 40px 20px;
    text-align: center;
    box-shadow: 0 -15px 40px rgba(0, 0, 0, 0.3);
    margin-top: 60px;
}

.footer .container {
    max-width: 1200px;
    margin: 0 auto;
}

.footer p {
    font-size: 16px;
    color: #bbb;
}

/* Responsive Styles */
@media screen and (max-width: 768px) {
    .bus-card {
        max-width: 100%;
        margin: 0 auto;
    }

    h1 {
        font-size: 32px;
    }

    .bus-title {
        font-size: 24px;
    }

    .book-button {
        font-size: 18px;
        padding: 12px 22px;
    }
}

@media screen and (max-width: 480px) {
    h1 {
        font-size: 28px;
    }

    .bus-title {
        font-size: 22px;
    }

    .footer p {
        font-size: 14px;
    }

    .book-button {
        font-size: 16px;
        padding: 12px 20px;
    }
}

</style>
</head>
